package org.kie.workbench.common.stunner.core.backend.util;

import java.io.InputStream;

import org.junit.Test;

import static org.junit.Assert.assertEquals;

public class URLUtilsTest {

    private static final String IMAGE_PATH = "org/kie/workbench/common/stunner/core/backend/email.gif";
    private static final String IMAGE_DATA_URI = "data:image/gif;base64,";

    @Test
    public void buildImageDataUriTest() throws Exception {
        String dataUri = URLUtils.buildDataURIFromStream("email.gif",
                                                         loadStream(IMAGE_PATH));
        assertEquals(IMAGE_DATA_URI, dataUri);
    }

    private static InputStream loadStream(String path) {
        return Thread.currentThread().getContextClassLoader().getResourceAsStream(path);
    }
}
